import React, { useState, useEffect } from "react";
import { Helmet } from "react-helmet-async";
import AdminNavbar from "../../components/layout/AdminNavbar";
import ImageUpload from "../../components/common/ImageUpload";
import { API_ENDPOINTS } from "../../config/api";
import {
  PlusIcon,
  PencilIcon,
  TrashIcon,
  EyeIcon,
  MagnifyingGlassIcon,
  FunnelIcon,
  XMarkIcon,
  PhotoIcon,
} from "@heroicons/react/24/outline";

const AdminProducts = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState("");
  const [statusFilter, setStatusFilter] = useState("all");
  const [categoryFilter, setCategoryFilter] = useState("all");
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [showModal, setShowModal] = useState(false);
  const [showViewModal, setShowViewModal] = useState(false);
  const [viewingProduct, setViewingProduct] = useState(null);
  const [editingProduct, setEditingProduct] = useState(null);
  const [categories, setCategories] = useState([]);
  const [successMessage, setSuccessMessage] = useState(null);
  const [submitting, setSubmitting] = useState(false);

  // Form state
  const [formData, setFormData] = useState({
    name: "",
    description: "",
    price: "",
    stock: "",
    category: "",
    sku: "",
    isActive: true,
    isFeatured: false,
    images: [],
  });

  useEffect(() => {
    fetchProducts();
    fetchCategories();
  }, [currentPage, searchTerm, statusFilter, categoryFilter]);

  const fetchProducts = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem("token");
      const params = new URLSearchParams({
        page: currentPage,
        limit: 10,
        ...(searchTerm && { search: searchTerm }),
        ...(statusFilter !== "all" && { status: statusFilter }),
        ...(categoryFilter !== "all" && { category: categoryFilter }),
      });

      const response = await fetch(
        `${API_ENDPOINTS.ADMIN_PRODUCTS}?${params}`,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
        }
      );

      if (!response.ok) {
        throw new Error("Failed to fetch products");
      }

      const data = await response.json();
      setProducts(data.data.products);
      setTotalPages(data.data.pagination.totalPages);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const fetchCategories = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await fetch(API_ENDPOINTS.CATEGORIES, {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
      });

      if (response.ok) {
        const data = await response.json();
        setCategories(data.data.categories || []);
      }
    } catch (err) {
      console.error("Failed to fetch categories:", err);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Debug: Check if form data is actually populated
    console.log('=== FORM SUBMIT TRIGGERED ===');
    console.log('Current formData state:', formData);

    // Debug: Form submitted successfully

    try {
      setSubmitting(true);
      setError(null); // Clear any previous errors
      const token = localStorage.getItem("token");
      const url = editingProduct
        ? API_ENDPOINTS.PRODUCT_BY_ID(editingProduct._id)
        : API_ENDPOINTS.PRODUCTS;

      const method = editingProduct ? "PUT" : "POST";

      // Create FormData for file uploads
      const formDataToSend = new FormData();

      // Add text fields - handle empty values safely
      formDataToSend.append("name", formData.name || "");
      formDataToSend.append("description", formData.description || "");
      formDataToSend.append("price", formData.price ? parseFloat(formData.price).toString() : "0");
      formDataToSend.append("stock", formData.stock ? parseInt(formData.stock).toString() : "0");
      formDataToSend.append("category", formData.category || "");
      formDataToSend.append("sku", formData.sku || "");
      formDataToSend.append("isActive", formData.isActive.toString());
      formDataToSend.append("isFeatured", formData.isFeatured.toString());

      // Debug: Log what we're sending
      console.log('=== FRONTEND SUBMIT DEBUG ===');
      console.log('formData state:', formData);
      console.log('URL:', url);
      console.log('Method:', method);
      console.log('Token exists:', !!token);
      console.log('FormData entries:');
      for (let [key, value] of formDataToSend.entries()) {
        console.log(key, ':', value);
      }
      console.log('==========================');


      // Add images if any
      if (formData.images && formData.images.length > 0) {
        // Filter only new images (files) to upload
        const newImages = formData.images.filter(
          (img) => img.isNew && img.file
        );

        if (newImages.length > 0) {
          // If editing and we have existing images, check if we need to replace them
          if (
            editingProduct &&
            editingProduct.images &&
            editingProduct.images.length > 0
          ) {
            formDataToSend.append("replaceImages", "true");
          }

          // Add each new image file
          newImages.forEach((image) => {
            formDataToSend.append("images", image.file);
          });
        }
      }

      console.log('Making API call to:', url);
      const response = await fetch(url, {
        method,
        headers: {
          Authorization: `Bearer ${token}`,
          // Don't set Content-Type, let browser set it with boundary for FormData
        },
        body: formDataToSend,
      });

      console.log('Response status:', response.status);
      console.log('Response ok:', response.ok);

      if (!response.ok) {
        const errorData = await response.json();
        console.log('Error response:', errorData);

        // Handle validation errors specifically
        if (errorData.errors && Array.isArray(errorData.errors)) {
          const errorMessages = errorData.errors.map(err => err.message).join(', ');
          setError(`Validation failed: ${errorMessages}`);
        } else {
          setError(errorData.message || "Failed to save product");
        }
        return;
      }

      const responseData = await response.json();
      console.log('Success response:', responseData);

      setShowModal(false);
      setEditingProduct(null);
      setFormData({
        name: "",
        description: "",
        price: "",
        stock: "",
        category: "",
        sku: "",
        isActive: true,
        isFeatured: false,
        images: [],
      });
      setSuccessMessage(editingProduct ? "Product updated successfully!" : "Product created successfully!");
      setTimeout(() => setSuccessMessage(null), 3000); // Clear success message after 3 seconds
      fetchProducts();
    } catch (err) {
      console.error('Submit error:', err);
      setError(err.message);
    } finally {
      setSubmitting(false);
    }
  };

  const handleView = (product) => {
    setViewingProduct(product);
    setShowViewModal(true);
  };

  const handleEdit = (product) => {
    setEditingProduct(product);

    // Convert existing images to the new format
    const existingImages = (product.images || []).map((img) => ({
      ...img,
      isNew: false, // Existing images are not new
    }));

    setFormData({
      name: product.name,
      description: product.description,
      price: product.price,
      stock: product.stock,
      category: product.category._id,
      sku: product.sku,
      isActive: product.isActive,
      isFeatured: product.isFeatured,
      images: existingImages,
    });
    setShowModal(true);
  };

  const handleDelete = async (productId) => {
    if (!window.confirm("Are you sure you want to delete this product?")) {
      return;
    }

    try {
      const token = localStorage.getItem("token");
      const response = await fetch(API_ENDPOINTS.PRODUCT_BY_ID(productId), {
        method: "DELETE",
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
      });

      if (!response.ok) {
        throw new Error("Failed to delete product");
      }

      fetchProducts();
    } catch (err) {
      setError(err.message);
    }
  };

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;

    // Debug: Log input changes
    console.log('Input changed:', { name, value, type, checked });

    setFormData((prev) => {
      const newData = {
        ...prev,
        [name]: type === "checkbox" ? checked : value,
      };
      console.log('Updated formData:', newData);
      return newData;
    });

    // Clear error when user starts typing
    if (error) {
      setError(null);
    }
  };

  const resetForm = () => {
    setFormData({
      name: "",
      description: "",
      price: "",
      stock: "",
      category: "",
      sku: "",
      isActive: true,
      isFeatured: false,
      images: [],
    });
    setEditingProduct(null);
    setShowModal(false);
    setError(null); // Clear any errors when resetting form
    setSuccessMessage(null); // Clear any success messages when resetting form
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-blue-600"></div>
      </div>
    );
  }

