import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { fetchCategories, clearRateLimit, forceRefresh } from '../../store/slices/categorySlice';
import Button from './Button';

const DebugPanel = () => {
  const dispatch = useDispatch();
  const { categories, isRateLimited, retryAfter, lastFetched, loading, error } = useSelector((state) => state.categories);

  const handleForceRefresh = () => {
    dispatch(forceRefresh());
    dispatch(fetchCategories({ forceRefresh: true }));
  };

  const handleClearRateLimit = () => {
    dispatch(clearRateLimit());
  };

  const formatTime = (timestamp) => {
    if (!timestamp) return 'Never';
    return new Date(timestamp).toLocaleTimeString();
  };

  if (process.env.NODE_ENV !== 'development') {
    return null;
  }

  return (
    <div className="bg-gray-100 border border-gray-300 rounded-lg p-4 mb-4">
      <h3 className="text-lg font-semibold mb-2">Debug Panel - Categories</h3>
      <div className="grid grid-cols-2 gap-4 text-sm">
        <div>
          <strong>Categories Count:</strong> {categories.length}
        </div>
        <div>
          <strong>Loading:</strong> {loading ? 'Yes' : 'No'}
        </div>
        <div>
          <strong>Rate Limited:</strong> {isRateLimited ? 'Yes' : 'No'}
        </div>
        <div>
          <strong>Retry After:</strong> {retryAfter ? `${retryAfter}s` : 'N/A'}
        </div>
        <div>
          <strong>Last Fetched:</strong> {formatTime(lastFetched)}
        </div>
        <div>
          <strong>Error:</strong> {error || 'None'}
        </div>
      </div>
      <div className="mt-4 flex gap-2">
        <Button
          onClick={handleForceRefresh}
          variant="outline"
          size="sm"
        >
          Force Refresh
        </Button>
        <Button
          onClick={handleClearRateLimit}
          variant="outline"
          size="sm"
        >
          Clear Rate Limit
        </Button>
        <Button
          onClick={() => dispatch(fetchCategories())}
          variant="outline"
          size="sm"
        >
          Fetch Categories
        </Button>
      </div>
    </div>
  );
};

export default DebugPanel;
